#' \if{latex}{\figure{mdl_percola_gr4.pdf}{options: width=140mm}}
#' 
#' \mjsdeqn{k^* = 1 - \left[ 1 + \left(\frac{4}{9} \frac{W_{soil}}{C_{soil}} \right)^4 \right]^{-1/4}}
